import './App.css';
import 'semantic-ui-css/semantic.min.css';
import {
  Form,
  TextArea,
  Button,
} from 'semantic-ui-react';

import axios from 'axios';
import React, { useState, useEffect } from 'react';

// console.log("hello", process.env.REACT_APP_OPENAI_API_KEY)

function App() {
  const [inputText, setInputText] = useState('');

  const { Configuration, OpenAIApi } = require("openai");
  const configuration = new Configuration({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  });
  const openai = new OpenAIApi(configuration);

  const getOpenAIResponse = () => {
    openai.createCompletion({
      model: "text-davinci-002",
      prompt: inputText,
      max_tokens: 291,
      temperature: 0.7,
    }).then((res) => {
      console.log('STOP');
      console.log(res);
    })
  };

  return (
    <div className="app-header">
      <h2 className="header">Texty Translator</h2>
      <div className='app-body'>
        <div>
            <Form>
              <h3>List any number of keywords you want in your synopsis. Ex: man, woman, dog, McDonald's</h3>
                <Form.Field
                    control={TextArea}
                    placeholder="Keywords"
                    onChange={(e) => setInputText(e.target.value)}
                />

                <h3>Choose length of film.</h3>
                <select className="language-select">
                    <option>10 minutes</option>
                    <option>30 minutes</option>
                    <option>1 hour</option>
                    <option>2 hours</option>
                </select>

                <Form.Field
                    control={TextArea}
                    placeholder='Your synopsis...'
                />

                <Button color="pink" size="large" margin='10px 30px' onClick={getOpenAIResponse}>
                  Translate
                </Button>
            </Form>
        </div>
      </div>
    </div>
  );
}

export default App;
