import './App.css';
import 'semantic-ui-css/semantic.min.css';
import {
  Form,
  TextArea,
  Button,
  Icon
} from 'semantic-ui-react';

function App() {
  return (
    <div className="app-header">
      <h2 className="header">Texty Translator</h2>
      <div className='app-body'>
        <div>
            <Form>
                <Form.Field
                    control={TextArea}
                    placeholder="List any number of keywords. Ex: man, woman, dog, McDonald's"
                />

                <select className="language-select">
                    <option>Please Select Language..</option>
                </select>

                <Form.Field
                    control={TextArea}
                    placeholder='Your Result Translation..'
                />

                <Button
                    color="orange"
                    size="large"
                >
                    <Icon name='translate' />
                    Translate</Button>
            </Form>
        </div>
      </div>
    </div>
  );
}

export default App;
