import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { Form, TextArea, Button, Container } from 'semantic-ui-react';
import React, { useState } from 'react';

function App() {
  const [inputText, setInputText] = useState('');
  const [inputLength, setInputLength] = useState('');
  const [inputTheme, setInputTheme] = useState('');
  const [resultText, setResultText] = useState('');

  const { Configuration, OpenAIApi } = require("openai");
  const configuration = new Configuration({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  });
  const openai = new OpenAIApi(configuration);

  const getOpenAIResponse = () => {
    openai.createCompletion({
      model: "text-davinci-002",
      prompt: `Keywords: ${inputText}\nWrite a harmless synopsis for a ${inputLength} long ${inputTheme} film that includes all of the keywords.\nSynopsis:`,
      max_tokens: 256,
      temperature: 0.7,
    }).then((res) => {
      console.log(res);
      console.log('stop')
      setResultText(res.data.choices[0].text.replace(/\n/g, ''));
    }).catch((err) => {
      console.log(err);
    });
  };

  return (
    <Container>
      <div className="app-header">
        <h2 className="header">Movie Synopsis Generator</h2>
      </div>
      <div className='app-body'>
            <Form>
              <h3>List any number of keywords you want in your synopsis. Ex: man, woman, dog, McDonald's</h3>
                <Form.Field control={TextArea} placeholder="Keywords" onChange={(e) => setInputText(e.target.value)}/>

                <h3>Choose length of film.</h3>
                <select className="language-select" onChange={(e) => setInputLength(e.target.value)}>
                    <option>10 minutes</option>
                    <option>30 minutes</option>
                    <option>1 hour</option>
                    <option>2 hours</option>
                </select>

                <h3>Choose genre of film.</h3>
                <select className="language-select" onChange={(e) => setInputTheme(e.target.value)}>
                    <option>Drama</option>
                    <option>Comedy</option>
                    <option>Action</option>
                    <option>Science fiction</option>
                    <option>Adventure</option>
                </select>

                <Button className="button" color="pink" size="large" margin='10px 30px' onClick={getOpenAIResponse}>
                  Make a film!
                </Button>

                <Form.Field
                    control={TextArea}
                    // placeholder='Your synopsis...'
                    value={resultText}
                />
            </Form>
        </div>
    </Container>
  );
}

export default App;
