import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { Form, TextArea, Button, Container } from 'semantic-ui-react';
import React, { useState } from 'react';

function App() {
  const [inputText, setInputText] = useState('');
  const [inputLength, setInputLength] = useState('');
  const [inputTheme, setInputTheme] = useState('');
  const [resultText, setResultText] = useState('');
  const [isSynopsis, setIsSynopsis] = useState(null);
  const prompt1 = `Keywords: ${inputText}\nWrite a harmless synopsis for a ${inputLength} long ${inputTheme} film that includes all of the keywords.\nSynopsis:`
  const prompt2 = `Keywords: ${inputText}\n

                  Write a short excerpt from an interesting film with a plot that incorporates all of the keywords. Present the excerpt in proper script format.\n

                  Excerpt:`

  const { Configuration, OpenAIApi } = require("openai");
  const configuration = new Configuration({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  });
  const openai = new OpenAIApi(configuration);

  const getOpenAIResponse = () => {
    openai.createCompletion({
      model: "text-davinci-002",
      prompt: `${isSynopsis ? prompt1 : prompt2}`,
      max_tokens: 256,
      temperature: 0.7,
    }).then((res) => {
      console.log(res);
      console.log('stop')
      // .replace(/\n/g, '')
      setResultText(res.data.choices[0].text);
    }).catch((err) => {
      console.log(err);
    });
  };

  const setMode = (e) => {
    console.log("e.target.value:", e.target.value);
    if (e.target.value === 'synopsis') {
      console.log('clicked synopsis');
      setIsSynopsis((prev) => !prev);
      console.log(isSynopsis);
    } else if (e.target.value === 'excerpt') {
      console.log('clicked excerpt');
      setIsSynopsis((prev) => !prev);
      console.log(isSynopsis);
    }
    getOpenAIResponse();
  }

  return (
    <Container className='container' textAlign='center'>
      <div className="app-header">
        <h2 className="header">Movie Plot Generator</h2>
      </div>
      <div className='app-body'>
            <Form>
              <h3>List any number of keywords you want in your synopsis or script excerpt. Ex: man, woman, dog, McDonald's</h3>
                <Form.Field control={TextArea} placeholder="Enter keywords for your synopsis..." onChange={(e) => setInputText(e.target.value)}/>

                <h3>Choose length of film.</h3>
                <select onChange={(e) => setInputLength(e.target.value)}>
                    <option>10 minutes</option>
                    <option>30 minutes</option>
                    <option>1 hour</option>
                    <option>2 hours</option>
                </select>

                <h3>Choose genre of film.</h3>
                <select onChange={(e) => setInputTheme(e.target.value)}>
                    <option>Drama</option>
                    <option>Comedy</option>
                    <option>Action</option>
                    <option>Science fiction</option>
                    <option>Adventure</option>
                </select>

                <Button size='large' value='synopsis' onClick={setMode}>
                  Make a synopsis!
                </Button>

                <Button size='large' value='excerpt' onClick={setMode}>
                  Make an excerpt!
                </Button>


                <h2>Synopsis:</h2>
                <textarea
                    readOnly='readonly'
                    value={resultText}
                    rows='25'
                />
            </Form>
        </div>
    </Container>
  );
}

export default App;
